#ifndef BLINKIT_BLINK_WEB_GESTURE_EVENT_H
#define BLINKIT_BLINK_WEB_GESTURE_EVENT_H

#pragma once

namespace blink {

class WebGestureEvent
{
public:
    enum ScrollUnits {
        kPrecisePixels = 0,  // generated by high precision devices.
        kPixels,             // large pixel jump duration; should animate to delta.
        kPage,               // page (visible viewport) based scrolling.
        kLastScrollUnit = kPage,
    };
};

} // namespace blink

#endif // BLINKIT_BLINK_WEB_GESTURE_EVENT_H
